﻿window.onload = function () {
    showHighlight();
    var a = new wheelnav("indexDiv", null, 500, 500);
    (a.slicePathFunction = slicePath().WheelSlice),
        (a.colors = colorpalette.fractallove),
        (a.sliceHoverTransformFunction = sliceTransform().ScaleTitleTransform),
        (a.sliceSelectedTransformFunction = sliceTransform().ScaleTitleTransform),
        document.documentElement.clientWidth < 990 &&
            ((a.navAngle = 90),
            (a.titleAttr = { font: "100 18px Impact, Charcoal, sans-serif", fill: "#111", cursor: "pointer", stroke: "none" }),
            (a.titleSelectedAttr = { font: "100 18px Impact, Charcoal, sans-serif", fill: "#FFF", cursor: "pointer" })),
        a.createWheel([
            "imgsrc:images/programming.png",
            "imgsrc:images/robotronics.png",
            "imgsrc:images/ecell.png",
            "imgsrc:images/yantrik.png",
            "imgsrc:images/nirmaan.png",
            "imgsrc:images/stac.png",
            "imgsrc:images/KBG.png",
            "imgsrc:images/Sae.png",
        ]),
        createTabHandler(a.navItems[0], "tabDefaultNav", "tabProgramming"),
        createTabHandler(a.navItems[1], "tabDefaultNav", "tabRobotronics"),
        createTabHandler(a.navItems[2], "tabDefaultNav", "tabEcell"),
        createTabHandler(a.navItems[3], "tabDefaultNav", "tabYantrik"),
        createTabHandler(a.navItems[4], "tabDefaultNav", "tabNirmaan"),
        createTabHandler(a.navItems[5], "tabDefaultNav", "tabStac"),
        createTabHandler(a.navItems[6], "tabDefaultNav", "tabKbg"),
        createTabHandler(a.navItems[7], "tabDefaultNav", "tabSae");   


};

